import React, { useState, useEffect } from 'react';

function CalendarView({ holidays, holidayType }) {
    const [currentMonth, setCurrentMonth] = useState(new Date().getMonth());
    const [currentYear, setCurrentYear] = useState(new Date().getFullYear());

    // Function to get the number of days in a month
    const daysInMonth = (month, year) => new Date(year, month + 1, 0).getDate();

    // Function to get the first day of the month (0 = Sunday, 6 = Saturday)
    const firstDayOfMonth = (month, year) => new Date(year, month, 1).getDay();

    // Generate days for the current month view
    const generateCalendarDays = () => {
        const numDays = daysInMonth(currentMonth, currentYear);
        const firstDay = firstDayOfMonth(currentMonth, currentYear); // 0 = Sunday, 1 = Monday ...
        const days = [];

        // Fill leading empty days (previous month's days)
        // Adjust for desired start of week (e.g., Monday = 0)
        const startDayIndex = (firstDay === 0) ? 6 : firstDay - 1; // If Sunday is 0, make it 6 for Monday start (ISO)
                                                                 // Otherwise, just subtract 1.
                                                                 // For simplicity, let's stick to default JS getDay() for now.
                                                                 // If you want Monday as first day, adjust this.
                                                                 // For now, it will start with Sunday as the first column.
        for (let i = 0; i < firstDay; i++) { // Using firstDay directly for 0=Sunday start
            days.push({ date: null, isPlaceholder: true });
        }

        // Fill current month's days
        for (let i = 1; i <= numDays; i++) {
            days.push({ date: new Date(currentYear, currentMonth, i), isPlaceholder: false });
        }

        return days;
    };

    const getHolidayDetailsForDate = (date) => {
        const matchingHolidays = [];
        const targetDate = date.toDateString(); // "Mon Jul 21 2025"

        if (holidayType === 'public') {
            holidays.forEach(holiday => {
                const holidayDate = new Date(holiday.date).toDateString();
                if (holidayDate === targetDate) {
                    matchingHolidays.push({ name: holiday.holiday, type: 'public' });
                }
            });
        } else if (holidayType === 'school') {
            holidays.forEach(holiday => {
                const startDate = new Date(holiday.starts.replace(/\(.*\)/, '').trim());
                const endDate = holiday.finishes ? new Date(holiday.finishes.replace(/\(.*\)/, '').trim()) : startDate;

                // Check if the date falls within the holiday period (inclusive)
                if (date >= startDate && date <= endDate) {
                    matchingHolidays.push({ name: holiday.type, type: 'school' });
                }
            });
        }
        return matchingHolidays;
    };


    const goToPreviousMonth = () => {
        setCurrentMonth(prev => (prev === 0 ? 11 : prev - 1));
        if (currentMonth === 0) {
            setCurrentYear(prev => prev - 1);
        }
    };

    const goToNextMonth = () => {
        setCurrentMonth(prev => (prev === 11 ? 0 : prev + 1));
        if (currentMonth === 11) {
            setCurrentYear(prev => prev + 1);
        }
    };

    const monthNames = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    const dayNames = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"]; // For calendar header

    const calendarDays = generateCalendarDays();

    return (
        <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-xl">
            <div className="flex justify-between items-center mb-4">
                <button
                    onClick={goToPreviousMonth}
                    className="p-2 rounded-md bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-gray-200 hover:bg-gray-300 dark:hover:bg-gray-600"
                    aria-label="Previous month"
                >
                    &lt; Prev
                </button>
                <h3 className="text-xl font-semibold text-gray-900 dark:text-gray-100">
                    {monthNames[currentMonth]} {currentYear}
                </h3>
                <button
                    onClick={goToNextMonth}
                    className="p-2 rounded-md bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-gray-200 hover:bg-gray-300 dark:hover:bg-gray-600"
                    aria-label="Next month"
                >
                    Next &gt;
                </button>
            </div>

            <div className="grid grid-cols-7 gap-1 text-center font-bold text-sm mb-2">
                {dayNames.map(day => (
                    <div key={day} className="text-gray-700 dark:text-gray-300 p-2 border-b border-gray-200 dark:border-gray-700">{day}</div>
                ))}
            </div>

            <div className="grid grid-cols-7 gap-1">
                {calendarDays.map((day, index) => {
                    if (day.isPlaceholder) {
                        return <div key={`placeholder-${index}`} className="p-2 bg-gray-100 dark:bg-gray-700 rounded"></div>;
                    }

                    const today = new Date();
                    const isToday = day.date.toDateString() === today.toDateString();
                    const holidaysOnThisDay = getHolidayDetailsForDate(day.date);

                    let dayClasses = "p-2 rounded text-gray-900 dark:text-gray-100 flex flex-col items-center justify-center h-20";
                    let holidayIndicatorClasses = "";

                    if (isToday) {
                        dayClasses += " border-2 border-purple-500 dark:border-purple-400";
                    }

                    if (holidaysOnThisDay.length > 0) {
                        const isPublicHoliday = holidaysOnThisDay.some(h => h.type === 'public');
                        const isSchoolHoliday = holidaysOnThisDay.some(h => h.type === 'school');

                        if (isPublicHoliday && isSchoolHoliday) {
                            // If both, maybe a mixed color or specific indicator
                            holidayIndicatorClasses = "bg-gradient-to-br from-blue-500 to-green-500 text-white";
                        } else if (isPublicHoliday) {
                            holidayIndicatorClasses = "bg-blue-500 text-white"; // Blue for public holidays
                        } else if (isSchoolHoliday) {
                            holidayIndicatorClasses = "bg-green-500 text-white"; // Green for school holidays
                        }
                        dayClasses += ` ${holidayIndicatorClasses} shadow-md`;
                    } else {
                        dayClasses += " bg-gray-50 dark:bg-gray-700 hover:bg-gray-100 dark:hover:bg-gray-600";
                    }


                    return (
                        <div key={day.date.toISOString()} className={dayClasses}>
                            <span className="font-bold text-lg">{day.date.getDate()}</span>
                            {holidaysOnThisDay.length > 0 && (
                                <div className="text-xs mt-1 text-center break-words max-h-10 overflow-hidden">
                                    {holidaysOnThisDay.map((h, i) => (
                                        <span key={i} className="block leading-tight" title={h.name}>
                                            {h.name}
                                        </span>
                                    ))}
                                </div>
                            )}
                        </div>
                    );
                })}
            </div>
            {/* Legend for colors */}
            <div className="mt-6 flex flex-wrap justify-center gap-4 text-sm">
                <div className="flex items-center">
                    <span className="inline-block w-4 h-4 bg-blue-500 rounded-full mr-2"></span>
                    <span className="text-gray-700 dark:text-gray-300">Public Holiday</span>
                </div>
                <div className="flex items-center">
                    <span className="inline-block w-4 h-4 bg-green-500 rounded-full mr-2"></span>
                    <span className="text-gray-700 dark:text-gray-300">School Holiday</span>
                </div>
                <div className="flex items-center">
                    <span className="inline-block w-4 h-4 bg-gradient-to-br from-blue-500 to-green-500 rounded-full mr-2"></span>
                    <span className="text-gray-700 dark:text-gray-300">Both</span>
                </div>
                <div className="flex items-center">
                    <span className="inline-block w-4 h-4 border-2 border-purple-500 rounded-full mr-2"></span>
                    <span className="text-gray-700 dark:text-gray-300">Today</span>
                </div>
            </div>
        </div>
    );
}

export default CalendarView;
